import firebase from "firebase";
import "firebase/firestore";
import { USER_STATE_CHANGE } from "../constants";

export function fetchUser() {
  return (dispatch) => {
    const uid = firebase.auth().currentUser.uid;
    firebase
      .firestore()
      .collection("user")
      .doc(uid)
      .get()
      .then((snapshot) => {
        snapshot.exists ? dispatch({ type: USER_STATE_CHANGE, currentUser: snapshot.data }) : console.log("does not exist");
      });
  };
}
